import styled from "styled-components";

export const LongButton = styled.button`
  text-decoration: none;
  border: none;
  width: 90%;
  font-size: 1.2em;
  color: #429629;
  box-sizing: border-box;
  border-radius: 5px;
  padding: 0.5em;
  margin: 2em 0em;
  background-color: white;
  border: 2px solid #429629;
  font-weight: 600;

  &:hover {
    background-color: #429629;
    color: white;
  }
`;
