import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { createTaskStart } from "../../redux/all-tasks/all-tasks.actions";
import { fetchCurrentProjectStart } from "../../redux/current-project/current-project.actions";
import { signOut } from "../../redux/user/user.actions";
import {
  Container,
  InfoInput,
  InfoTitle,
  InfoDiv,
  LongInput,
  LowerNavDiv,
  OneHalfDiv,
  NavBar,
  OneThirdDiv,
  Overlay,
} from "./create-task-form.styles";
import { withRouter } from "react-router-dom";
import { selectCreateTaskStart } from "../../redux/all-tasks/all-tasks.selectors";
import { selectCreateTaskSuccessful } from "./../../redux/all-tasks/all-tasks.selectors";
import { createStructuredSelector } from "reselect";
import Spinner from "../spinner/spinner.component";

class CreateTaskForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      taskDetails: {
        taskName: "",
        taskStartDate: "",
        taskEndDate: "",
        projectId: this.props.match.params.projectId.toString(),
      },
      active: "Info",
    };
  }

  handleOnChange = (e) => {
    const { name, value } = e.target;
    let task = this.state.taskDetails;
    task[name] = value;

    this.setState({ taskDetails: task });
    console.log(this.state);
  };

  createTask = async (e) => {
    e.preventDefault();
    let taskDetails = this.state.taskDetails;
    console.log(taskDetails);
    this.props.createTask(taskDetails);
    this.props.history.goBack();
  };

  handleOnClick = (e) => {
    let { key } = e.target.dataset;

    this.setState({
      active: key,
    });
  };

  render() {
    let { active } = this.state;
    let { projectId } = this.state.taskDetails;

    if (this.props.createOrganisationStart && !this.props.createdOrganisation) {
      return <Spinner />;
    } else if (this.props.createdOrganisation) {
      this.exit();
    }

    return (
      <Overlay>
        <NavBar>
          <OneThirdDiv align="flex-start" color="#205284" onClick={this.exit}>
            <Link
              to={`/project/${projectId}`}
              style={{ textDecoration: "none", color: "#205284" }}
            >
              Cancel
            </Link>
          </OneThirdDiv>
          <OneThirdDiv align="center" style={{ fontWeight: "600" }}>
            New Task
          </OneThirdDiv>
          <OneThirdDiv
            align="flex-end"
            color="#205284"
            onClick={this.createTask}
          >
            Save
          </OneThirdDiv>
        </NavBar>
        <LowerNavDiv>
          <OneHalfDiv
            data-key="Info"
            onClick={this.handleOnClick}
            active={active === "Info"}
          >
            INFO
          </OneHalfDiv>
          <OneHalfDiv
            data-key="Steps"
            onClick={this.handleOnClick}
            active={active === "Steps"}
          >
            STEPS
          </OneHalfDiv>
        </LowerNavDiv>

        {this.state.active === "Info" ? (
          <form
            style={{ display: "flex", flexDirection: "column" }}
            onChange={(e) => this.handleOnChange(e)}
          >
            <div>
              <LongInput
                type="text"
                value={this.state.taskDetails.taskName}
                name="taskName"
                id="taskName"
                placeholder="Task Title"
                onChange={(e) => this.handleOnChange(e)}
                required
              />
            </div>

            <Container>
              <InfoDiv>
                <InfoTitle>Start Date</InfoTitle>
                <InfoInput
                  type="date"
                  value={this.state.taskDetails.taskStartDate}
                  name="taskStartDate"
                  id="taskStartDate"
                  onChange={(e) => this.handleOnChange(e)}
                  required
                />
              </InfoDiv>

              <InfoDiv>
                <InfoTitle>End Date</InfoTitle>
                <InfoInput
                  type="date"
                  value={this.state.taskDetails.taskEndDate}
                  name="taskEndDate"
                  id="taskEndDate"
                  onChange={(e) => this.handleOnChange(e)}
                  required
                />
              </InfoDiv>
            </Container>
          </form>
        ) : (
          <div></div>
        )}
      </Overlay>
    );
  }
}

const mapStateToProps = createStructuredSelector({
  createTaskStart: selectCreateTaskStart,
  createTaskSucessful: selectCreateTaskSuccessful,
});

const mapDispatchToProps = (dispatch) => ({
  signOut: () => dispatch(signOut()),
  createTask: (taskDetails) => dispatch(createTaskStart(taskDetails)),
  fetchProject: (projectId) => dispatch(fetchCurrentProjectStart(projectId)),
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withRouter(CreateTaskForm));
