import React, { Component } from "react";
import { connect } from "react-redux";
import { createTaskStart} from "../../redux/all-tasks/all-tasks.actions";
import { fetchCurrentProjectStart } from "../../redux/current-project/current-project.actions";
import { signOut } from "../../redux/user/user.actions";
import {LongInput, LowerNavDiv, OneHalfDiv, NavBar, OneThirdDiv, Overlay} from "./create-task-form.styles"


class CreateTaskForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      taskDetails: {
        taskName: "",
        taskStartDate: "",
        taskEndDate: "",
        projectId: this.props.projectId.toString(),
      },
      active: "Steps"
    };
  }

  handleOnChange = (e) => {
    const { name, value } = e.target;
    let task = this.state.taskDetails;
    task[name] = value;

    this.setState({ taskDetails: task });
  };

  createTask = async (e) => {
    e.preventDefault();
    let taskDetails = this.state.taskDetails;
    await this.props.createTask(taskDetails);
  };

  handleOnClick = (e) => {
    let {key} = e.target.dataset

    this.setState({
        active: key
    })
}

  render() {
    let {active} = this.state
    return (
      <Overlay>
        <NavBar>
          <OneThirdDiv align="flex-start" color="#205284">
            Cancel
          </OneThirdDiv>
          <OneThirdDiv align="center" style={{fontWeight: "600"}}>
            New Task
          </OneThirdDiv>
          <OneThirdDiv align="flex-end" color="#205284">
            Save
          </OneThirdDiv>
        </NavBar>
        <LowerNavDiv>
            <OneHalfDiv data-key='Steps' onClick={this.handleOnClick} active={active === 'Steps'} >
                STEPS
            </OneHalfDiv>
            <OneHalfDiv  data-key='Info' onClick={this.handleOnClick} active={active === 'Info'}>
                INFO
            </OneHalfDiv>
        </LowerNavDiv>
        
        <form
          style={{ display: "flex", flexDirection: "column" }}
          onChange={(e) => this.handleOnChange(e)}
          onSubmit={this.createTask}
        >
          <div>
            <LongInput
              type="text"
              value={this.state.taskDetails.taskName}
              name="taskName"
              id="taskName"
              placeholder="Task Title"
              onChange={(e) => this.handleOnChange(e)}
              required/>
          </div>

          <div>
            <label htmlFor="taskStartDate"> Task Start Date: </label>
            <input
              type="date"
              value={this.state.taskDetails.taskStartDate}
              name="taskStartDate"
              id="taskStartDate"
              onChange={(e) => this.handleOnChange(e)}
              required/>
          </div>

          <div>
            <label htmlFor="taskEndDate"> Task End Date: </label>
            <input
              type="date"
              value={this.state.taskDetails.taskEndDate}
              name="taskEndDate"
              id="taskEndDate"
              onChange={(e) => this.handleOnChange(e)}
              required/>
          </div>
          
          <div>
            <input type="submit" value="Create Task" />
   
          </div>
        </form>

      </Overlay>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  signOut: () => dispatch(signOut()),
  createTask: (taskDetails) => dispatch(createTaskStart(taskDetails)),
  fetchProject: (projectId) => dispatch(fetchCurrentProjectStart(projectId))
});

export default connect(null, mapDispatchToProps)(CreateTaskForm);
