import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { createTaskStart } from "../../redux/all-tasks/all-tasks.actions";
import { fetchCurrentProjectStart } from "../../redux/current-project/current-project.actions";
import { signOut } from "../../redux/user/user.actions";
import {
  Container,
  InfoInput,
  InfoTitle,
  InfoDiv,
  LongInput,
  LowerNavDiv,
  OneHalfDiv,
  NavBar,
  OneThirdDiv,
  Overlay,
} from "./costbook-createcategory.styles";
import { withRouter } from "react-router-dom";

class CostBookCreateCategory extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  render() {
    return (
      <Overlay>
        <NavBar>
          <OneThirdDiv align="flex-start" color="#205284">
            <Link
              to={`/costbook`}
              style={{ textDecoration: "none", color: "#205284" }}
            >
              Cancel
            </Link>
          </OneThirdDiv>
          <OneThirdDiv align="center" style={{ fontWeight: "600" }}>
            New Category
          </OneThirdDiv>
          <OneThirdDiv
            align="flex-end"
            color="#205284"
            onClick={this.props.createCategory}
          >
            Save
          </OneThirdDiv>
        </NavBar>

        <form
          style={{ display: "flex", flexDirection: "column" }}
          onChange={(e) => this.handleOnChange(e)}
        >
          <div>
            <LongInput
              type="text"
              value={this.state.taskDetails.taskName}
              name="taskName"
              id="taskName"
              placeholder="Task Title"
              onChange={(e) => this.handleOnChange(e)}
              required
            />
          </div>

          <Container>
            <InfoDiv>
              <InfoTitle>Start Date</InfoTitle>
              <InfoInput
                type="date"
                value={this.state.taskDetails.taskStartDate}
                name="taskStartDate"
                id="taskStartDate"
                onChange={(e) => this.handleOnChange(e)}
                required
              />
            </InfoDiv>

            <InfoDiv>
              <InfoTitle>End Date</InfoTitle>
              <InfoInput
                type="date"
                value={this.state.taskDetails.taskEndDate}
                name="taskEndDate"
                id="taskEndDate"
                onChange={(e) => this.handleOnChange(e)}
                required
              />
            </InfoDiv>
          </Container>
        </form>
      </Overlay>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  signOut: () => dispatch(signOut()),
  createTask: (taskDetails) => dispatch(createTaskStart(taskDetails)),
  fetchProject: (projectId) => dispatch(fetchCurrentProjectStart(projectId)),
});

export default connect(
  null,
  mapDispatchToProps
)(withRouter(CostBookCreateCategory));
