import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { createStructuredSelector } from "reselect";
import {
  createCostBookCategoryStart,
  createCostCodeStart,
  fetchCostBookStart,
} from "../../redux/costbook/costbook.actions";

import CostBookCreateCategory from "../../components/costbook-createcategory/costbook-createcategory";

class CreateCostBookCategory extends Component {
  constructor() {
    super();
    this.state = {
      category: {
        categoryName: null,
      },
    };
  }

  categoryChange = (e) => {
    const { name, value } = e.target;
    //console.log(name, value)
    let categoryName = this.state.category;
    categoryName[name] = value;
    this.setState({ category: categoryName }, () => {
      console.log(this.state);
    });
  };

  createCategory = (e) => {
    e.preventDefault();
    let costBookDetails = this.state.category;
    this.props.createCategory(costBookDetails);
  };

  render() {
    //console.log(this.props.costbook);
    return (
      <>
        <CostBookCreateCategory
          createCategory={this.createCategory}
          handleOnChange={this.categoryChange}
        />
        <h3>CREATE CATEGORY FORM</h3>
        <form
          onSubmit={this.createCategory}
          onChange={this.categoryChange}
          style={{ border: "1px solid black" }}
        >
          <label name="categoryName" id="categoryName">
            Category Name
          </label>
          <input type="text" name="categoryName" id="categoryName" />
          <button type="submit">CREATE CATEGORY</button>
        </form>
      </>
    );
  }
}

const mapStateToProps = createStructuredSelector({});

const mapDispatchToProps = (dispatch) => ({
  createCategory: (costBookDetails) =>
    dispatch(createCostBookCategoryStart(costBookDetails)),
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(CreateCostBookCategory);
