import React, { Component } from 'react';
import { gantt } from 'dhtmlx-gantt';
import 'dhtmlx-gantt/codebase/dhtmlxgantt.css';
import { selectCurrentProjectTasks } from '../../redux/current-project/current-project.selectors';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';

const data = {
    data: [
        { id: 1, text: 'Task #1', start_date: '15-04-2019', duration: 3, progress: 0.6 },
        { id: 2, text: 'Task #2', start_date: '18-04-2019', duration: 3, progress: 0.4 }
    ]
};
 
class GanttChart extends Component {

    constructor(props) {
        super(props);
    }

    dataProcessor = null;

    componentDidMount() {
        
        gantt.init(this.ganttContainer);
        this.initGanttDataProcessor();

        

        gantt.parse(data);
    }

    initGanttDataProcessor(props) {
        /**
         * type: "task"|"link"
         * action: "create"|"update"|"delete"
         * item: data object object
         */
        const onDataUpdated = this.props.onDataUpdated;
        
        this.dataProcessor = gantt.createDataProcessor((type, action, item, id) => {
          console.log(type, action, item, id);
          
        });
      }

    componentWillUnmount() {
        if (this.dataProcessor) {
            this.dataProcessor.destructor();
            this.dataProcessor = null;
        }
    }

    render() {
       return (
           <div
                ref={ (input) => { this.ganttContainer = input } }
                style={ { width: '100%', height: '100vh' } }
            ></div>
       );
    }
}

const mapStateToProps = createStructuredSelector({
    tasks: selectCurrentProjectTasks
  });
  
  const mapDispatchToProps = (dispatch) => ({
      
    
  });
  
  export default connect(mapStateToProps, mapDispatchToProps)(GanttChart);
