import styled from "styled-components";

export const FormDiv = styled.form`
 @media screen and (min-width: 320px) {
    display: flex;
    flex-direction: column;
    
  }
`;

export const FormLabel= styled.label`
 @media screen and (min-width: 320px) {
    width:100%;
    font-size:0.9em;
    color: #666666;
    
  }
`;
