import React from "react";
import {Link} from "react-router-dom";
import { ProjectDiv, LeftDiv, RightDiv, BigText, SmallText} from "./task-item.styles";

export const TaskItem = ({taskName, taskStartDate, taskEndDate, completedSteps, totalSteps, completionPercentage, _id, isTaskDone}) => {
    return (
        <Link to={`/task/${_id}`} style={{textDecoration:'none'}}> 
            <ProjectDiv key={id}>
                <LeftDiv>
                    <BigText>{taskName}</BigText>
                    <SmallText>TownHome</SmallText>
                </LeftDiv>
                <RightDiv>
                    <BigText>$5000</BigText>
                    <SmallText>Remodel</SmallText>
                </RightDiv>
            </ProjectDiv>
        </Link>
    )
}
