import React, { Component } from "react";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
import { createProjectStart } from "../redux/project/project.actions";
import { signOut } from "../redux/user/user.actions";
import { selectCurrentUserFirstName } from "../redux/user/user.selectors";

class ProfilePage extends Component {
  constructor() {
    super();
    this.state = {
      projectDetails: {
        projectName: "",
        projectStatus: "",
      },
    };
  }

  signOut = () => {
    const { history, signOut } = this.props;
    signOut();
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    history.push("/login");
  };

  handleOnChange = (e) => {
    const { name, value } = e.target;
    let project = this.state.project;
    project[name] = value;

    this.setState({ projectDetails: project });
  };

  createProject = (e) => {
    e.preventDefault();
    this.props.createProject(this.state.projectDetails);
  };

  render() {
    const { name } = this.props;
    return (
      <div>
        <h1>hi {name}!</h1>
        <form
          style={{ display: "flex", flexDirection: "column" }}
          onChange={(e) => this.handleOnChange(e)}
          onSubmit={this.createProject}
        >
          <div>
            <label htmlFor="projectName"> Role: </label>
            <select
              value={this.state.resp.role}
              name="role"
              id="role"
              onChange={(e) => this.handleOnChange(e)}
              required
            >
              <option value="">Please choose an option</option>
              <option value="PROJECT_OWNER">Project Owner</option>
              <option value="TRADE_PARTNER">Trade Partner</option>
              <option value="TASK_OWNER">Task Owner</option>
            </select>
          </div>

          <div>
            <label htmlFor="username"> Username: </label>
            <input
              type="text"
              value={this.state.resp.username}
              name="username"
              id="username"
              onChange={(e) => this.handleOnChange(e)}
              required
            />
          </div>

          <div>
            <input type="submit" value="Create Project" />
          </div>
        </form>

        <button onClick={this.signOut}>Sign out</button>
      </div>
    );
  }
}

const mapStateToProps = createStructuredSelector({
  name: selectCurrentUserFirstName,
});

const mapDispatchToProps = (dispatch) => ({
  signOut: () => dispatch(signOut()),
  createProject: () => dispatch(createProjectStart()),
});

export default connect(mapStateToProps, mapDispatchToProps)(ProfilePage);
