import styled from "styled-components";

export const POItemMenuDiv = styled.div`
  display: flex;
  flex-direction: row;
  padding: 0.5em 1em;
  border-bottom: 1px solid #808080;
`;

export const POItemMenuItem = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
`;
