import React from "react";

class SignUpForm extends React.Component {
  constructor() {
    super();
    this.state = {
      resp: {
        firstName: "",
        lastName: "",
        phoneNumber: "",
        email: "",
        password: "",
        role: "",
        username: "",
      },
      confirmPassword: "",
    };
  }

  handleOnChange = (e) => {
    const { name, value } = e.target;
    if(name === "confirmPassword"){
      this.setState({confirmPassword:value});
    }
    else {
        let response = this.state.resp;
        response[name] = value;

        this.setState({resp: response}, () => {console.log(this.state.resp);});
    }
    
  };

  render() {
    return (
      <div style={{ margin: "10px" }}>
        <form
          style={{ display: "flex", flexDirection: "column" }}
          onChange={(e) => this.handleOnChange(e)}
        >
          <div>
            <label for="firstName"> First Name: </label>
            <input
              type="text"
              value={this.state.firstName}
              name="firstName"
              id="firstName"
            />
          </div>

          <div>
            <label for="lastName"> Last Name: </label>
            <input
              type="text"
              value={this.state.lastName}
              name="lastName"
              id="lastName"
            />
          </div>

          <div>
            <label for="email"> Email: </label>
            <input
              type="email"
              value={this.state.email}
              name="email"
              id="email"
            />
          </div>

          <div>
            <label for="phoneNumber"> Phone Number: </label>
            <input
              type="number"
              value={this.state.phoneNumber}
              name="phoneNumber"
              id="phoneNumber"
            />
          </div>

          <div>
            <label for="role"> Role: </label>
            <select value={this.state.role} name="phoneNumber" id="phoneNumber">
              <option value="">Please choose an option</option>
              <option value="PROJECT_OWNER">Project Owner</option>
              <option value="TRADE_PARTNER">Trade Partner</option>
              <option value="TASK_OWNER">Task Owner</option>
            </select>
          </div>

          <div>
            <label for="username"> Username: </label>
            <input
              type="text"
              value={this.state.username}
              name="username"
              id="username"
            />
          </div>

          <div>
            <label for="password"> Password: </label>
            <input
              type="password"
              value={this.state.password}
              name="password"
              id="password"
            />
          </div>

          <div>
            <label for="confirmPassword"> Confirm Password: </label>
            <input
              type="password"
              value={this.state.confirmPassword}
              name="confirmPassword"
              id="confirmPassword"
            />
          </div>
        </form>
      </div>
    );
  }
}

export default SignUpForm;
