import React from "react";
import {Link} from "react-router-dom";
import { TaskItem } from "../task-item/task-item.component";


export default function TaskList({tasks}) {
    console.log(tasks);
    if(!tasks){
      tasks = []
    }
  return (
    <div>
        {tasks.map(({ taskName, taskStartDate, taskEndDate, completedSteps, totalSteps, completionPercentage, _id, isTaskDone },index) => {
          const taskDetails = {taskName, taskStartDate, taskEndDate, completedSteps, totalSteps, completionPercentage, _id, isTaskDone};
          return (  
            <div>
              <TaskItem taskDetails= {taskDetails}/>

              <div key={_id} style = {{ padding: '10px', border:'1px solid black'}}>
              <Link to={`/task/${_id}`}> <h3>{taskName} ({taskStartDate} to {taskEndDate})</h3> </Link>
              steps: {completedSteps}/{totalSteps}<br/>
              Completion Percentage: {completionPercentage}
              {
                isTaskDone?<div>TASK IS COMPLETED</div> : null
              }
              </div>

            </div>
            
          );
        })}
    </div>
  );
}
