import React, {Component} from "react";
import {Link} from "react-router-dom";
import { StepDiv, LeftDiv, RightDiv, BigText, SmallText, Circle, RowContainer, ArrowDiv, PartitionDiv} from "./step-item.styles";
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';
import { deleteCurrentTaskStart } from "../../redux/current-task/current-task.actions";
import { completeStepQuestionStart, completeStepStart, fetchCurrentStepStart } from '../../redux/current-step/current-step.actions'

class StepItem extends Component{
    constructor(props){
        super(props);
      
        this.state = {
            stepDetails: this.props.stepDetails,
            isOpen:false
        };
    }

    submitQuestion = () => {
        console.log("submit question")
        const {stepId, completeStepQuestion} = this.props
        completeStepQuestion(stepId)
        this.props.fetchCurrentStep(stepId)
        //window.location.reload()
    }

    completeStep = async() => {
        const {stepId, completeStep, taskId, history} = this.props
        const data = {stepId, taskId}
        console.log(data)
        await completeStep(data)
        history.push(`/task/${taskId}`)
        
    }

    stepDetailsDiv = ({stepId, isStepDone,questionStatement,isStepQuestionAnswered, stepCompletionMessage}) => {
        return(
            <div>
                 {
                questionStatement?
                <div>
                    Question: {questionStatement}<br/>
                    {
                        isStepQuestionAnswered? <div>Question has been answered</div> : <button onClick={this.submitQuestion()}>COMPLETE STEP QUESTION</button>
                    }
                    
                </div> 
                : null
                }
                {/* {
                    isStepDone ? <div>STEP COMPLETED</div> : <button onClick={this.completeStep()}>COMPLETE STEP</button>
                }
                <h3>{stepCompletionMessage}</h3> */}
            </div>
        )
        

    };
    
    render (){
        const {stepName,questionStatement,_id,isStepDone,isQuestionAnswered, index} = this.state.stepDetails;
        
        return (
            <StepDiv>
                <PartitionDiv key={_id}>
                    <LeftDiv>
                            <RowContainer>
                            <Circle /> 
                            <SmallText>STEP - {index+1}</SmallText>
                            </RowContainer>
                            
                            <BigText>{stepName}</BigText>
                    </LeftDiv>
                    <RightDiv>
                        <button id={_id}>Delete</button>
                       
                    </RightDiv>
                </PartitionDiv>
                {

                    this.state.isOpen?<div>{this.stepDetailsDiv(this.state.stepDetails)}</div>:null
                }
                <ArrowDiv onClick={()=>
                {
                    this.setState((prevState) => ({isOpen: !prevState.isOpen }));
                    
                }}>
                    {!this.state.isOpen? <p>&or;</p> : <p>&and;</p>}
                </ArrowDiv>

                
            
            </StepDiv>
            
                
            
        
    )}
}

const mapStateToProps = createStructuredSelector({
    
  });
  
  const mapDispatchToProps = (dispatch) => ({
    deleteTask : (taskId) => dispatch(deleteCurrentTaskStart(taskId)),
    fetchCurrentStep : (stepId) => dispatch(fetchCurrentStepStart(stepId)),
    completeStepQuestion: (stepId) => dispatch(completeStepQuestionStart(stepId)),
    completeStep: (data) => dispatch(completeStepStart(data))
  });
  
  export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(StepItem);
