import React, { Component } from 'react'
import { Text, FormHeading, ManageRolesDiv} from './manage-roles.styles'

class ManageRoles extends Component {
    constructor(){
        
        super()
        this.state = {
           addRoleSwitch: false
        }
    }

    addRoleToggle = () =>
    {
        this.setState((prevState) => ({
           addRoleSwitch: !prevState.addRoleSwitch
        }));
    }

    render() {
        
        return (
            <ManageRolesDiv>
                <FormHeading>
                    Manage Team
                    <button  onClick={this.props.toggleManageRoles} style={{textDecoration:'none', 
                                    background: 'none', 
                                    border: 'none', 
                                    fontSize:'1.4em', 
                                    color:'rgba(102,102,102,0.6)'}}> &times;</button>
                </FormHeading>

                <Text 
                    onClick={this.addRoleToggle}
                    style={{
                            color:"#2F80CB",
                            fontSize:"1em"}}>
                <u><b>Add Role</b></u>
                </Text>

                {this.state.manageRolesSwitch? 
                <Overlay backgroundcolor = "rgba(0,0,0,0.3)" style={{bottom:"0",height:"100%"}}>
                <ManageRoles toggleManageRoles={this.manageRolesToggle}></ManageRoles>
                </Overlay> : <Overlay/>
                }



            </ManageRolesDiv>
        )
    }
}

export default ManageRoles;
