import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { createTaskStart } from "../../redux/all-tasks/all-tasks.actions";
import { fetchCurrentProjectStart } from "../../redux/current-project/current-project.actions";
import { signOut } from "../../redux/user/user.actions";
import {
  Container,
  InfoInput,
  InfoTitle,
  InfoDiv,
  LongInput,
  LowerNavDiv,
  OneHalfDiv,
  NavBar,
  OneThirdDiv,
  Overlay,
} from "./costbook-createcostcode.styles";
import { withRouter } from "react-router-dom";
import {
  createCostBookCategoryStart,
  createCostCodeStart,
  fetchCostBookStart,
} from "../../redux/costbook/costbook.actions";
import { createStructuredSelector } from "reselect";
import { selectCostBookDetails } from "../../redux/costbook/costbook.selectors";
import DropDown from "./form-dropdown/form-dropdown.component";

class CostBookCreateCostCode extends Component {
  constructor(props) {
    super(props);
    this.state = {
      costCode: {
        costCodeTitle: null,
        categoryId: null,
      },
    };
  }

  onCostCodeSubmit = (e) => {
    e.preventDefault();
    let costCodeDetails = this.state.costCode;
    this.props.createCostCode(costCodeDetails);
  };

  handleOnChange = (e) => {
    const { name, value } = e.target;
    let costcodeinfo = this.state.costCode;
    costcodeinfo[name] = value;
    this.setState({ costCode: costcodeinfo });
    console.log(this.state);
  };

  exit = () => {
    this.props.history.goBack();
  };

  render() {
    let categoryNames = ["None"];
    this.props.costbook.map((cost) => {
      return categoryNames.append(cost.categoryName);
    });
    return (
      <Overlay>
        <NavBar>
          <OneThirdDiv align="flex-start" color="#205284" onClick={this.exit}>
            Cancel
          </OneThirdDiv>
          <OneThirdDiv align="center" style={{ fontWeight: "600" }}>
            New Cost Code
          </OneThirdDiv>
          <OneThirdDiv
            align="flex-end"
            color="#205284"
            onClick={this.onCostCodeSubmit}
          >
            Save
          </OneThirdDiv>
        </NavBar>

        <form
          style={{ display: "flex", flexDirection: "column" }}
          onChange={(e) => this.handleOnChange(e)}
        >
          <div>
            <LongInput
              type="text"
              value={this.state.costCode.costCodeTitle}
              name="costCodeTitle"
              id="costCodeTitle"
              placeholder="Cost Code Title"
              onChange={(e) => this.handleOnChange(e)}
              required
            />
          </div>

          <select id="categoryId" name="categoryId">
            <option>none</option>
            {this.props.costbook.map((cost) => {
              return <option value={cost._id}>{cost.categoryName}</option>;
            })}
          </select>

          <DropDown
            key="categoryId"
            options={categoryNames}
            selected={categoryNames[0]}
            onChange={this.handleOnChange}
          />
        </form>
      </Overlay>
    );
  }
}

const mapStateToProps = createStructuredSelector({
  costbook: selectCostBookDetails,
});

const mapDispatchToProps = (dispatch) => ({
  createCostCode: (costCodeDetails) =>
    dispatch(createCostCodeStart(costCodeDetails)),
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withRouter(CostBookCreateCostCode));
