import styled from "styled-components";

export const Overlay = styled.div`
    @media screen and (min-width: 320px) {
      height: 100vh;
      background-color: #cab5b5;
      width: 100%;
      position: fixed; 
      z-index: 1; 
      left: 0;
      bottom: -100;
      overflow-x: hidden; 
      transition: 0.2s;  
      display:flex;
      flex-direction:column;
      box-sizing: border-box;
      bottom: 0;

    }
`
