import styled from "styled-components";

export const ColumnDiv = styled.div`
  display: flex;
  padding: 1em;
  flex-direction: column;
  width: 100%;
  box-sizing: border-box;
  color: #666666;
  font-size: 1em;
`;

export const CostCodeTitle = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  width: 100%;
`;

export const LongInput = styled.input`
  width: 100%;
  height: 2.5em;
  font-size: 1em;
  border: none;
  border-top: 1px solid #d3d3d3;
  border-bottom: 1px solid #d3d3d3;
  margin-top: 1em;
  padding: 0em 1em;

  &:focus {
    outline: none;
  }
`;

export const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
`;
export const InfoDiv = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  width: 80%;
  margin-top: 1em;
`;

export const InfoTitle = styled.div`
  font-size: 0.9em;
  flex: 0.5;
  font-weight: bold;
  color: #555555;
`;

export const InfoInput = styled.input`
  font-size: 0.9em;
  flex: 0.5;
  background-color: #f4f6f9;
  border-radius: 5px;
  box-shadow: 0px 5px 8px 0px rgba(0, 0, 0, 0.12);
  padding: 1em 0.5em;
  border: none;

  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;

  &:focus {
    outline: none;
  }
`;
