import React from 'react'
import { StepNavDiv, OneThirdDiv } from './step-lower-nav.styles'
import StepListContainer from '../steps-list/steps-list.container';

class StepLowerNav extends React.Component{

    constructor(props){
        super(props)
        this.state = {
            active: 'Steps'
        }
    }

    handleOnClick = (e) => {
        let {key} = e.target.dataset

        this.setState({
            active: key
        })
    }

    showActiveDiv = () => {
        let {active} = this.state

        if (active === 'Steps'){
            return <StepListContainer steps={this.props.steps}/>
        }
    }

    render(){
        let {active} = this.state

        return (
            <>
                <StepNavDiv>
                    <OneThirdDiv data-key='Steps' onClick={this.handleOnClick} active={true} >
                        STEPS
                    </OneThirdDiv>
                    <OneThirdDiv  data-key='Info' onClick={this.handleOnClick}>
                        INFO
                    </OneThirdDiv>
                    <OneThirdDiv  data-key='Log' onClick={this.handleOnClick}>
                        LOG
                    </OneThirdDiv>
                </StepNavDiv>
                {this.showActiveDiv()}
            </>
            )
    }
   
}

export default StepLowerNav;
