import React, {Component} from "react";
import {Link} from "react-router-dom";
import { ProjectDiv, LeftDiv, RightDiv, BigText, SmallText, Circle, ProgressBar, Progress, ProgressDiv} from "./step-item.styles";
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';
import { deleteCurrentTaskStart } from "../../redux/current-task/current-task.actions";

class StepItem extends Component{
    constructor(props){
        super(props);
      
        this.state = {
            stepDetails: this.props.stepDetails
        };
    }

    // deleteTask(e){
    //     console.log("delete button", this.state.taskDetails._id);
    //     this.props.deleteTask(this.state.taskDetails._id);
    // }
    
    render (){
        const {stepName,questionStatement,_id,isStepDone,isQuestionAnswered, index} = this.state.stepDetails;
        return (
            <div key={_id}>
                <Link to={`/step/${_id}`} style={{textDecoration:'none'}}>
                    <Circle /> 
                    <SmallText>STEP - {index+1}</SmallText>
                    <BigText>{stepName}</BigText>
                </Link>
                <button id={_id} onClick={(e)=>this.deleteStep(e)}>Delete</button>
                {questionStatement ? (
                  <>
                    {isQuestionAnswered ? (
                      <div>Question has been answered</div>
                    ) : null}
                  </>
                ) : null}
                {isStepDone ? <div>Step Completed!</div> : null}
              </div>
        
    )}
}

const mapStateToProps = createStructuredSelector({
    
  });
  
  const mapDispatchToProps = (dispatch) => ({
    deleteTask : (taskId) => dispatch(deleteCurrentTaskStart(taskId))
  });
  
  export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(StepItem);
