import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { createTaskStart } from "../../redux/all-tasks/all-tasks.actions";
import { fetchCurrentProjectStart } from "../../redux/current-project/current-project.actions";
import { signOut } from "../../redux/user/user.actions";
import {
  Container,
  InfoInput,
  InfoTitle,
  InfoDiv,
  LongInput,
  LowerNavDiv,
  OneHalfDiv,
  NavBar,
  OneThirdDiv,
  Overlay,
} from "./costbook-createcostcode.styles";
import { withRouter } from "react-router-dom";
import {
  createCostBookCategoryStart,
  createCostCodeStart,
  fetchCostBookStart,
} from "../../redux/costbook/costbook.actions";

class CostBookCreateCostCode extends Component {
  constructor(props) {
    super(props);
    this.state = {
      costCode: {
        costCodeTitle: null,
        categoryId: null,
      },
    };
  }

  onCostCodeSubmit = (e) => {
    e.preventDefault();
    let costCodeDetails = this.state.costCode;
    this.props.createCostCode(costCodeDetails);
  };

  onCostCodeChange = (e) => {
    const { name, value } = e.target;
    let costcodeinfo = this.state.costCode;
    costcodeinfo[name] = value;
    this.setState({ costCode: costcodeinfo });
    console.log(this.state);
  };

  exit = () => {
    this.props.history.goBack();
  };

  render() {
    return (
      <Overlay>
        <NavBar>
          <OneThirdDiv align="flex-start" color="#205284" onClick={this.exit}>
            Cancel
          </OneThirdDiv>
          <OneThirdDiv align="center" style={{ fontWeight: "600" }}>
            New Category
          </OneThirdDiv>
          <OneThirdDiv
            align="flex-end"
            color="#205284"
            onClick={this.createCategory}
          >
            Save
          </OneThirdDiv>
        </NavBar>

        <form
          style={{ display: "flex", flexDirection: "column" }}
          onChange={(e) => this.handleOnChange(e)}
        >
          <div>
            <LongInput
              type="text"
              value={this.state.category.categoryName}
              name="categoryName"
              id="categoryName"
              placeholder="Category Name"
              onChange={(e) => this.handleOnChange(e)}
              required
            />
          </div>
        </form>

        <h3>CREATE CATEGORY FORM</h3>
        <form
          style={{ border: "1px solid black" }}
          onChange={this.onCostCodeChange}
          onSubmit={this.onCostCodeSubmit}
        >
          <label name="costCodeTitle" id="costCodeTitle">
            Title
          </label>
          <input type="text" name="costCodeTitle" id="costCodeTitle" />
          <label name="categoryId" id="categoryId">
            Category
          </label>
          <select id="categoryId" name="categoryId">
            <option>none</option>
            {this.props.costbook.map((cost) => {
              return <option value={cost._id}>{cost.categoryName}</option>;
            })}
          </select>
          <button type="submit">CREATE COST CODE</button>
        </form>
      </Overlay>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  createCostCode: (costCodeDetails) =>
    dispatch(createCostCodeStart(costCodeDetails)),
});

export default connect(
  null,
  mapDispatchToProps
)(withRouter(CostBookCreateCostCode));
