import React from "react";
import {Link} from "react-router-dom";
import { ProjectDiv, LeftDiv, RightDiv, BigText, SmallText} from "./task-item.styles";

export const TaskItem = (taskDetails) => {
    return (
        <Link to={`/task/${taskDetails.taskDetails._id}`} style={{textDecoration:'none'}}> 
            <ProjectDiv key={taskDetails.taskDetails._id}>
                <LeftDiv>
                    <SmallText>Task-{taskDetails.taskDetails.index + 1}</SmallText>
                    <BigText>{taskDetails.taskDetails.taskName}</BigText>
                </LeftDiv>
                <RightDiv>
                    <Circle/>
                </RightDiv>
            </ProjectDiv>
        </Link>
    )
}
