import React, { Component } from 'react'
import {DropDownDiv, DropDownButton, DropDownContent, DropDownLink} from "./dropdown.styles";

class DropDown extends Component {
    constructor(){
        super();
        this.state = {
            dropDownSwitch: false
        }
    }

    DropDownToggle = () => {
        this.setState((prevState) => ({
            dropDownSwitch: !prevState.dropDownSwitch
        }));
        console.log('called')
        console.log(this.state.DropDownSwitch)
    }
    render() {
        return (
            <DropDownDiv>
                Hello
                <DropDownButton>
                    <DropDownContent display={this.state.dropDownSwitch?"block":"block"}>
                        <DropDownLink>Hello</DropDownLink>
                    </DropDownContent>
                </DropDownButton>
            </DropDownDiv>
        )
    }
}

export default DropDown;
