import React, { Component } from 'react'
import {SearchDiv, SearchBarDiv, SearchIcon, SearchInput, FilterDiv } from './search-bar.styles'
import search from '../../assets/search.png'
import {Overlay} from "../admin-panel-task-page/admin-panel-task-page.styles"



class SearchBar extends Component {
    constructor(props){
        super(props)
        this.state = {
            placeholder: this.props.placeholder,
            searchText: "",
            showFilter: false
        }
    }

    handleOnChange = (e) => {
        let {value} = e.target;
        this.setState({searchText: value});
        this.props.search(value);
        
    }

    handleFilter = (e) => {
        this.setState((prevState) => ({
            showFilter: !prevState.showFilter
          }));
    }

    render() {
        const {placeholder, searchText} = this.state
        return (
            <>
            <SearchDiv>
                <SearchBarDiv>
                    <SearchIcon src={search} />
                    <SearchInput 
                        type='text' 
                        placeholder={placeholder} 
                        value={searchText}
                        onChange={this.handleOnChange}/>
                    
                </SearchBarDiv>
                <FilterDiv onChange = {this.handleFilter}>FILTER</FilterDiv>
            </SearchDiv>

            {this.state.showFilter? 
                <Overlay 
                    backgroundcolor = "rgba(0,0,0,0.3)" 
                    style={{bottom:"0",height:"100%", display:"flex", flexDirection:"row", justifyContent:"center"}}>
                
                </Overlay> : <Overlay/>
            }

        </>
        )
    }
}

export default SearchBar
