import styled from "styled-components";

export const LongButton = styled.button`
  text-decoration: none;
  border: none;
  width: 80%;
  font-size: 1em;
  height: 2em;
  color: #429629;
  box-sizing: border-box;
  border-radius: 5px;
  padding: 1em;
  margin: 0.5em 0;
  background-color: white;
  border: 2px solid #429629;

  &:hover {
    background-color: #429629;
    color: white;
  }
`;
