import React from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { createStructuredSelector } from "reselect";
import { BigText, SmallText } from "../project-item/project-item.styles";
import SearchBar from "../search-bar/search-bar.component";
import {
  POSummaryDiv,
  PODetailsDiv,
  PODiv,
  POName,
  PONameDiv,
  PONumber,
  TinyText,
  POGreyHeading,
} from "./purchase-orders-list.styles";

class PurchaseOrderList extends React.Component {
  render() {
    let purchaseOrders = this.props.purchaseOrders;
    console.log(purchaseOrders);

    let openAmount = 0;

    purchaseOrders.map(({ totalOrderAmount, totalPaidAmount }) => {
      console.log(totalOrderAmount, totalPaidAmount);
      openAmount += totalOrderAmount - totalPaidAmount;
    });

    if (!purchaseOrders) {
      purchaseOrders = [];
    }
    return (
      <div>
        <POGreyHeading>
          <div>OPEN:{openAmount}</div>
          <div>STATUS</div>
        </POGreyHeading>
        {purchaseOrders.map(
          (
            { orderFrom, totalOrderAmount, totalPaidAmount, _id, PoTitle },
            index
          ) => {
            return (
              <Link
                to={`/purchaseOrder/${_id}`}
                style={{ textDecoration: "none" }}
              >
                <PODiv key={_id}>
                  <PONameDiv>
                    <TinyText>PO #{index + 1000}</TinyText>
                    <BigText>{PoTitle}</BigText>
                  </PONameDiv>
                  <PODetailsDiv>
                    <BigText>
                      {totalOrderAmount === totalPaidAmount ? "Close" : "Open"}
                    </BigText>
                  </PODetailsDiv>
                </PODiv>
              </Link>
            );
          }
        )}
      </div>
    );
  }
}

const mapStateToProps = createStructuredSelector({});

const mapDispatchToProps = (dispatch) => ({});

export default connect(mapStateToProps, mapDispatchToProps)(PurchaseOrderList);
