import React, { Component } from 'react'
import {DropDownDiv, DropDownButton, DropDownContent, DropDownOption, DropDownOptionHeading, DropDownOptionDescription} from "./dropdown.styles";
import DownArrow from "../../assets/down-arrow.png"

class DropDown extends Component {
    constructor(props){
        super();
        this.state = {
            dropDownSwitch: false
        }
    }

    // componentDidMount() {
