    //     //     dropDownSwitch: !prevState.dropDownSwitch
    //     // }));

    // }
    render() {
        return (
            <DropDownDiv ref={this.dropdownContainer}>
                <DropDownButton onClick={this.props.onClickHandler} img={DownArrow}>Hello</DropDownButton>
                    <DropDownContent display={this.props.dropDownSwitch?"block":"none"}>
                        <DropDownOption>
                            <DropDownOptionHeading>Owner</DropDownOptionHeading>
                            <DropDownOptionDescription>
                                Acting Admin of the Project (limit 1 per project)
                            </DropDownOptionDescription>
                        </DropDownOption>
                        <DropDownOption>Hello</DropDownOption>
                        <DropDownOption>Hello</DropDownOption>
                    </DropDownContent>
            </DropDownDiv>
        )
    }
}

export default DropDown;
