import react, {Component} from 'react';
import {BigCircle} from "./create-button.styles";

class CreateButton extends Component {
    constructor(){
        super()
        this.state ={
            createSwitch:false
        }
    }

    handleOnClick = () => {
        this.setState((prevState) => ({
            createSwitch: !prevState.createSwitch
        }));
    }

    render() {
        return (
            <div>
                <BigCircle onClick={this.handleOnClick}>
                    <p style={{fontSize:"2em", margin:"0"}}>+</p>
                </BigCircle>
                {this.state.addRoleSwitch? 
                <Overlay 
                    backgroundcolor = "rgba(0,0,0,0.3)" 
                    style={{bottom:"0",height:"100%", display:"flex", flexDirection:"row", justifyContent:"center"}}>
                <AddRoleForm exit={this.addRoleToggle}></AddRoleForm>
                </Overlay> : <Overlay/>
                }

            </div>
            
        )
    }
}

export default CreateButton;
