import React, { Component } from "react";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
import { createTaskStart} from "../../redux/all-tasks/all-tasks.actions";
import { signOut } from "../../redux/user/user.actions";
import { selectCurrentUserFirstName } from "../../redux/user/user.selectors";

class CreateTaskForm extends Component {
  constructor() {
    super();
    this.state = {
      taskDetails: {
        taskName: "",
        taskStartDate: "",
        taskEndDate: ""
      },
    };
  }

  handleOnChange = (e) => {
    const { name, value } = e.target;
    let task = this.state.taskDetails;
    task[name] = value;

    this.setState({ taskDetails: task });
  };

  createTask = (e) => {
    e.preventDefault();
    let taskDetails = this.state.taskDetails;
    this.props.createTask(taskDetails);
  };

  

  render() {
    const { name, tasks } = this.props;
    
    return (
      <div>
        <h1>hi {name}!</h1>
        <form
          style={{ display: "flex", flexDirection: "column" }}
          onChange={(e) => this.handleOnChange(e)}
          onSubmit={this.createTask}
        >
          <div>
            <label htmlFor="taskName"> Task Name: </label>
            <input
              type="text"
              value={this.state.taskDetails.taskName}
              name="taskName"
              id="taskName"
              onChange={(e) => this.handleOnChange(e)}
              required/>
          </div>
          <div>
            <label htmlFor="taskStatus"> Task Status: </label>
            <select
              value={this.state.taskDetails.taskStatus}
              name="taskStatus"
              id="taskStatus"
              onChange={(e) => this.handleOnChange(e)}
              required
            >
              <option value="">Please choose an option</option>
              <option value="ACTIVE">Active</option>
              <option value="PLANNED">Planned</option>
              <option value="COMPLETED">Completed</option>
            </select>
          </div>

          <div>
            <input type="submit" value="Create Task" />
   
          </div>
        </form>

        <button onClick={this.signOut}>Sign out</button>

      </div>
    );
  }
}

const mapStateToProps = createStructuredSelector({
  name: selectCurrentUserFirstName
});

const mapDispatchToProps = (dispatch) => ({
  signOut: () => dispatch(signOut()),
  createTask: (taskDetails) => dispatch(createTaskStart(taskDetails))
});

export default connect(mapStateToProps, mapDispatchToProps)(CreateTaskForm);
