import React, { Component } from 'react'
import {DropDownDiv, DropDownButton, DropDownContent, DropDownOption, DropDownOptionHeading, DropDownOptionDescription} from "./dropdown.styles";
import DownArrow from "../../assets/down-arrow.png"

class DropDown extends Component {
    constructor(props){
        super();
        this.dropdownContainer = React.createRef();
        this.state={
            dropDownSwitch:false
        }
    }

    componentDidMount() {
        document.addEventListener("mousedown", this.handleClickOutside);
    }

    componentWillUnmount() {
      document.removeEventListener("mousedown", this.handleClickOutside);
    }

    handleClickOutside = (event) => {
        if (
          this.dropdownContainer.current &&
          !this.dropdownContainer.current.contains(event.target)
        ) {
          this.setState({
            dropDownSwitch: false,
          });
        }
      };

    DropDownToggle = (e) => {
        e.preventDefault();
        this.setState((prevState) => ({
            dropDownSwitch: !prevState.dropDownSwitch
        }));

    }
    render() {
        console.log(this.props)
        let {options, selected} = this.props;
        console.log(selected)
        let selectedIndex = 0;
        options.map((option, index)=> {
            if(option[0] === selected){
                selectedIndex = index;
            }
        })
        return (
            <DropDownDiv ref={this.dropdownContainer}>
                <DropDownButton onClick={this.DropDownToggle} img={DownArrow}>{options[selectedIndex][1]}</DropDownButton>
                    <DropDownContent display={this.state.dropDownSwitch?"block":"none"}>

                        <DropDownOption backgroundFlag = {true}>
                        <DropDownOptionHeading>{option[selectedIndex][1]}</DropDownOptionHeading>
                        <DropDownOptionDescription>{option[selectedIndex][2]}</DropDownOptionDescription>
                        </DropDownOption>
                        
                            {
                            options.map((option) => {
                                let selectedFlag = false;
                                if(option[0] != selected){
                                    return(
                                        <DropDownOption backgroundFlag = {selectedFlag}>
                                            <DropDownOptionHeading>{option[1]}</DropDownOptionHeading>
                                            <DropDownOptionDescription>{option[2]}</DropDownOptionDescription>
                                        </DropDownOption>
                                    
                                    )
                                }
                                
                                
                            })
                            }
                    </DropDownContent>
            </DropDownDiv>
        )
    }
}

export default DropDown;
