import React, { Component } from "react";
import { Hamburger } from "../hamburger/hamburger.component";
import { NavBar, Heading, AddProjectDiv,OneThirdDiv, Overlay } from "./project-nav.styles";
import {AddUserFormHeading} from '../adduser-form/adduser-form.styles'
import { BigText } from '../project-item/project-item.styles';
import CreateProjectForm from "../create-project-form/create-project-form.component";

export default class ProjectNav extends Component {
    constructor(){
        super();
        this.state={
            addProject: false
        }
    }
    addProject = () => {
        if(this.state.addProject === false){
        this.setState({addProject: true}, () => console.log(this.state))
    }else{
        this.setState({addProject: false}, () => console.log(this.state))
    }
    }
  render() {
    const title = this.props.title;
    return (
        <>
      <NavBar>
        <OneThirdDiv>
          <Hamburger />
        </OneThirdDiv>
        <OneThirdDiv>
          <Heading> {title} </Heading>
        </OneThirdDiv>
        <OneThirdDiv>
          <AddProjectDiv onClick={this.addProject}>
          + Add Project
          </AddProjectDiv>
        </OneThirdDiv>
      </NavBar>
      {
          this.state.addProject ? 
          <Overlay style={{width:"100%"}}>
              <AddUserFormHeading>
                <BigText>Create a Project</BigText>
                <button style={{textDecoration:'none', background: 'none', border: 'none'}} onClick={this.addProject}><BigText>X</BigText></button>
            </AddUserFormHeading>
            <CreateProjectForm />
          </Overlay> : null
      }
        </>
    );
  }
}

