import styled from "styled-components";

export const Overlay = styled.div`
    @media screen and (min-width: 320px) {
      height: 100vh;
      background-color: #F5F5F5;
      width: 100%;
      position: fixed; 
      z-index: 1; 
      left: 0;
      bottom: -100;
      overflow-x: hidden; 
      transition: 0.2s;  
      display:flex;
      flex-direction:column;
      box-sizing: border-box;
      bottom: 0;

    }
`

export const NavBar = styled.div`
 @media screen and (min-width: 320px) {
    width:100%;
    height:3.5em;
    margin: 0;
    box-sizing:border-box;
    padding: 0em 1.5em;
    color: black;
    border: 1px solid #d3d3d3;
   
    display:flex;
    flex-direction:row;
    align-items: center;
    
  }
`;

export const OneThirdDiv = styled.div`
 @media screen and (min-width: 320px) {
    
    flex:1;
  
    display:flex;
    flex-direction:row;
    align-items: center;
    justify-content:${props=>props.align};
    color: ${props=>props.coloe}

 }
`;
