import React, { Component } from "react";
import { connect } from "react-redux";
import {
  Container,
  InfoInput,
  InfoTitle,
  InfoDiv,
  LongInput,
  LowerNavDiv,
  OneHalfDiv,
  NavBar,
  OneThirdDiv,
  Overlay,
} from "./costcode-createitem.styles";
import { withRouter } from "react-router-dom";
import {
  createCostBookCategoryStart,
  createCostCodeStart,
  fetchCostBookStart,
} from "../../redux/costbook/costbook.actions";
import { createStructuredSelector } from "reselect";
import { selectCostBookDetails } from "../../redux/costbook/costbook.selectors";
import DropDown from "./form-dropdown/form-dropdown.component";

class CostCodeCreateItem extends Component {
  constructor(props) {
    super(props);
    this.state = {
      item: {
        itemName: null,
        partNo: null,
        cost: null,
        description: null,
        itemLink: null,
        costCodeId: this.props.match.params.costCodeId,
        categoryId: this.props.match.params.categoryId,
      },
    };
  }

  onSubmit = (e) => {
    e.preventDefault();
    let itemDetails = this.state.item;
    this.props.createItem(itemDetails);
  };

  handleOnChange = (e) => {
    const { name, value } = e.target;
    console.log(name, value);
    let itemInfo = this.state.item;
    itemInfo[name] = value;
    this.setState({ item: itemInfo }, console.log(this.state.item));
  };

  exit = () => {
    this.props.history.goBack();
  };

  render() {
    let categoryNames = [];

    this.props.costbook.map((cost) => {
      return categoryNames.push(cost.categoryName);
    });

    return (
      <Overlay>
        <NavBar>
          <OneThirdDiv align="flex-start" color="#205284" onClick={this.exit}>
            Cancel
          </OneThirdDiv>
          <OneThirdDiv
            align="center"
            style={{ fontWeight: "600", fontSize: "0.9em" }}
          >
            New Cost Code
          </OneThirdDiv>
          <OneThirdDiv align="flex-end" color="#205284" onClick={this.onSubmit}>
            Save
          </OneThirdDiv>
        </NavBar>
        <form onChange={this.itemChange} onSubmit={this.createItem}>
          <label for="itemName" name="itemName" id="itemName">
            Item Name
          </label>
          <input name="itemName" id="itemName" type="text" />
          <br />
          <label for="partNo" name="partNo" id="partNo">
            Part No.
          </label>
          <input name="partNo" id="partNo" type="number" />
          <br />
          <label for="cost" name="cost" id="cost">
            Cost/part
          </label>
          <input name="cost" id="cost" type="number" />
          <br />
          <label for="itemLink" name="itemLink" id="itemLink">
            Item Link
          </label>
          <input name="itemLink" id="itemLink" type="text" />
          <br />
          <label for="description" name="description" id="description">
            Desciption
          </label>
          <input name="description" id="description" type="text" />
          <br />
          <button type="submit">CREATE ITEM</button>
        </form>
        <form
          style={{ display: "flex", flexDirection: "column" }}
          onChange={(e) => this.handleOnChange(e)}
        >
          <div>
            <InfoTitle style={{ margin: "1em 0em", fontSize: "1em" }}>
              Item Name
            </InfoTitle>
            <LongInput
              type="text"
              value={this.state.costCode.costCodeTitle}
              name="costCodeTitle"
              id="costCodeTitle"
              placeholder={this.state.costCode.costCodeTitle}
              onChange={(e) => this.handleOnChange(e)}
              required
            />
          </div>

          <InfoTitle style={{ margin: "1em 0em", fontSize: "1em" }}>
            Choose a cost category:
          </InfoTitle>
          <DropDown
            key="categoryId"
            options={categoryNames}
            selected={this.state.categoryName}
            onChange={this.handleOnChange}
            costbook={this.props.costbook}
          />

          <button
            style={{
              padding: "0.5em 1em",
              backgroundColor: "#6C7B8A",
              color: "white",
              fontSize: "1em",
              fontWeight: "600",
              border: "none",
              borderRadius: "4px",
              marginTop: "1em",
            }}
          >
            Save
          </button>
        </form>
      </Overlay>
    );
  }
}

const mapStateToProps = createStructuredSelector({
  costbook: selectCostBookDetails,
});

const mapDispatchToProps = (dispatch) => ({});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withRouter(CostCodeCreateItem));
