    flex-direction: column;
    align-items: center;
    color: rgba(0, 0, 0, 0.8);
    font-size: 1em;
  }
`;

export const TemplateScrollDiv = styled.div`
  @media screen and (min-width: 320px) {
    width: 100%;
    height: 60vh;
    overflow-x: scroll;
    -webkit-overflow-scrolling: touch;
    overflow-y: hidden;
    display: flex;
    flex-direction: row;
    flex-wrap: nowrap;

    &::-webkit-scrollbar {
      display: none;
    }
  }
`;

export const TemplateDiv = styled.div`
  @media screen and (min-width: 320px) {
    width: 80vw;
