import styled from "styled-components";

export const POSummaryDiv = styled.div`
@media screen and (min-width: 320px) {
    
    width:100%;
    height: 20%;
    display:flex;
    flex-direction:column;
    justify-content:center;
    align-items:center;

  }
`;

export const Text = styled.p`
@media screen and (min-width: 320px) {
    
    padding:0;
    margin:0;

  }
`;
