import styled from "styled-components";


export const NavBar = styled.div`
 @media screen and (min-width: 320px) {
    width:100%;
    font-family: 'Roboto', sans-serif;
    display:flex;
    flex-direction:row;
    align-items: center;
    justify-content: space-between;
    margin:10px;
  }
`;

export const Heading = styled.p`
 @media screen and (min-width: 320px) {
    margin:0;
  }
`;
