import React, { Component } from "react";
import { FormLabel,Container, FormDiv, Heading,FormButton } from "./projects-filter.styles";
import { connect } from "react-redux";
import { inviteUserStart, UpdateRolesInProjectStart } from "../../redux/current-project/current-project.actions";
import { createStructuredSelector } from "reselect";
import DropDown from "../projects-filter-dropdown/projects-filter-dropdown.component"
class ProjectsFilter extends Component {
  constructor() {
    super();
    this.state = {
      status:'',
      projectType:'',
      propertyType:'',

      allStatus: ['AllStatus','Active', 'Planned', 'Upcoming']

    };
  }

  handleOnChange = (e) => {
    const { name, value } = e.target;
    console.log(name, value)

   
  }

  handleOnSubmit = (e) => {
    e.preventDefault()
    
  }
  
  render() {
    let {projectUsers, projectRoles} = this.props;
    
    return (
        <>
        <Container>
        <Heading>
          Filters

          <button  onClick={this.props.exit} style={{textDecoration:'none', 
                                    background: 'none', 
                                    border: 'none', 
                                    fontSize:'1.4em', 
                                    color:'rgba(102,102,102,0.6)'}}> &times;</button>
        </Heading>
        
        <FormDiv>
          <FormLabel>
            Status
          </FormLabel>
          <DropDown 
          options={this.state.allStatus}
          selected="All Status"/>
        </FormDiv>

        <div>
        <FormButton 
            border="1px solid #000000"
            color="#3F8CFF"
            bgcolor = "#FFFFFF"
            width="40%"
            onClick={this.props.exit}
          > 
          Cancel
          </FormButton>
          <FormButton 
            border="1px solid #3F8CFF"
            color="#FFFFFF"
            bgcolor = "#3F8CFF"
            width="40%"
            onClick={this.handleOnSubmit}
          > 
          Create
          </FormButton>
        </div>
       
        </Container>
         
      </>

    );
  }
}

