import React, { Component } from "react";
import { connect } from "react-redux";
import {
  Container,
  InfoInput,
  InfoTitle,
  InfoDiv,
  LongInput,
  LowerNavDiv,
  OneHalfDiv,
  NavBar,
  OneThirdDiv,
  Overlay,
} from "./costcode-createitem.styles";
import { withRouter } from "react-router-dom";
import {
  createCostBookCategoryStart,
  createCostCodeStart,
  fetchCostBookStart,
} from "../../redux/costbook/costbook.actions";
import { createStructuredSelector } from "reselect";
import { selectCostBookDetails } from "../../redux/costbook/costbook.selectors";
import DropDown from "./form-dropdown/form-dropdown.component";

class CostCodeCreateItem extends Component {
  constructor(props) {
    super(props);
    this.state = {
      item: {
        itemName: null,
        partNo: null,
        cost: null,
        description: null,
        itemLink: null,
        costCodeId: this.props.match.params.costCodeId,
        categoryId: this.props.match.params.categoryId,
      },
    };
  }

  onCostCodeSubmit = (e) => {
    e.preventDefault();
    let costCodeDetails = this.state.costCode;

    this.exit();
  };

  handleOnChange = (e) => {
    const { name, value, catName } = e.target;
    let costcodeinfo = this.state.costCode;
    costcodeinfo[name] = value;
    this.setState({ costCode: costcodeinfo });
    console.log(this.state);

    if (name === "categoryId") {
      this.setState({ categoryName: catName });
    }
  };

  exit = () => {
    this.props.history.goBack();
  };

  render() {
    let categoryNames = [];

    this.props.costbook.map((cost) => {
      return categoryNames.push(cost.categoryName);
    });

    return (
      <Overlay>
        <NavBar>
          <OneThirdDiv align="flex-start" color="#205284" onClick={this.exit}>
            Cancel
          </OneThirdDiv>
          <OneThirdDiv
            align="center"
            style={{ fontWeight: "600", fontSize: "0.9em" }}
          >
            New Cost Code
          </OneThirdDiv>
          <OneThirdDiv
            align="flex-end"
            color="#205284"
            onClick={this.onCostCodeSubmit}
          >
            Save
          </OneThirdDiv>
        </NavBar>
        <form
          style={{ display: "flex", flexDirection: "column" }}
          onChange={(e) => this.handleOnChange(e)}
        >
          <div>
            <InfoTitle style={{ margin: "1em 0em", fontSize: "1em" }}>
              Title
            </InfoTitle>
            <LongInput
              type="text"
              value={this.state.costCode.costCodeTitle}
              name="costCodeTitle"
              id="costCodeTitle"
              placeholder={this.state.costCode.costCodeTitle}
              onChange={(e) => this.handleOnChange(e)}
              required
            />
          </div>

          <InfoTitle style={{ margin: "1em 0em", fontSize: "1em" }}>
            Choose a cost category:
          </InfoTitle>
          <DropDown
            key="categoryId"
            options={categoryNames}
            selected={this.state.categoryName}
            onChange={this.handleOnChange}
            costbook={this.props.costbook}
          />

          <button
            style={{
              padding: "0.5em 1em",
              backgroundColor: "#6C7B8A",
              color: "white",
              fontSize: "1em",
              fontWeight: "600",
              border: "none",
              borderRadius: "4px",
              marginTop: "1em",
            }}
          >
            Save
          </button>
        </form>
      </Overlay>
    );
  }
}

const mapStateToProps = createStructuredSelector({
  costbook: selectCostBookDetails,
});

const mapDispatchToProps = (dispatch) => ({});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withRouter(CostCodeCreateItem));
