import styled from 'styled-components'

export const AddRoleDiv = styled.div`
  
    transition: all 0.1s;
    width:80%;
    height:70%;
    border-radius:4px;
    background-color:white;
    margin-top:20%;
    padding:2em;
    box-sizing:border-box;

`;

export const UsersDiv = styled.div`
  
    border-radius: 4px;
    border: #666666 solid 1px;
    height: 8em;
    //overflow-y:scroll;

`;

export const UsersDivContent = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: flex-start;
    align-items: center;
    font-size:0.9em;
`;

export const FormButton = styled.button`
    border: 0.5px solid #BCC5D3; 
    box-sizing:border-box;
    height: 3em;
    width:100%;
    border-radius:4px;

    margin-top: 1em;
    font-size: 1em;
    color:#ffffff;
    background-color: #6C7B8A;

    padding: 0.5em;
`;
